	def dtFormat = "yyyy-MM-dd'T'HH:mm:ss.SSS'+07:00'";
	def date1 =date.format(dtFormat, TimeZone.getTimeZone('GMT+7'));
	
	def dtFormat2 = "yyyyMM";
	def date2 =date.format(dtFormat2, TimeZone.getTimeZone('GMT+7'));

	def dtFormat3 = "yyyyMMddHHmmssSSS";
	def date3 =date.format(dtFormat3, TimeZone.getTimeZone('GMT+7'));

	msg.put("input.timestamp",date1);
	msg.put("short.timestamp",date2);
	msg.put("datastatic.timestamp",date3);
	
	return true;         
}

